import React from 'react';
import API from '../utilities/api';

const Post = ({title, description, price, seller, location, id, updatePostList }) => {

    const [updatePost, setUpdatePost] = useState({});

    function handleUpdate(e) {
        setUpdatePost(e.target.value)
    }

    return (
        <div className="post">
            <div>
                <h3>{title}</h3>
                <p>{description}</p>
                <p>Seller: {seller}</p>
                <p>Price: {price}</p>
                <p>Location: {location}</p>
            </div>
            <div className="">
                <>
                    <button onClick={handleUpdate} className="edit">Edit</button>
                    <input title={title} description={description} price={price} seller={seller}></input>
                    <button className="delete">Delete</button>
                </>
            </div>
        </div>
    )
}

export default Post;
