// This will show all the Posts
// Contains Header, POSTS, followed by text posts, search input, and basic link for Add Post
// then it populates all the different posts
import React, { useState, useEffect } from 'react';
import ReactDOM from 'react-dom';
import API from '../utilities/api';
import Post from './Post';
import Drink from './Drink';

const Posts = () => {

    const [postList, setPostList] = useState([]); 

    const postElements = postList.map(post => <Post title={post.name}
                                                    description={post.amount}
                                                    price={post.type}
                                                    seller={post.seller}
                                                    location={post.location}/>)

    useEffect( async function () {
        try {
            const token = localStorage.getItem('vb-token');
            const response = await API.makeRequest(`/api/2105-OKU-RM-WEB-PT/posts`, 'GET');
            const data = await response.json();
            console.log(data);
            setPostList(data.data.posts);
        } catch (error) {
            console.error(error);
        }
    }, []);

    console.log(postList);

    const postElement = postList.map((post) => 
        <div>
            <h1>Title: {post.title}</h1>
            <h2>Description: {post.description}</h2>
        </div>
    )

    return (
        <div>
            {postElements}
        </div>
    )
}

export default Posts;
